INSERT INTO feedentry VALUES ('f7d7afe7-6927-3baf-90a7-e94e3ed1cde2', 'NRC Report: Oil, Misc: Motor in Septic System near Harrodsburg, KY', 'http://www.nrc.uscg.mil/reports/rwservlet?standard_web+inc_seq=1036703', 'Incident Type: FIXED - NRC Report ID: 1036703 - Medium Affected: WATER - Suspected Responsible Party: BOTTS GARAGE', '<b>Report Details</b><br/>NRC Report ID: <a href="http://www.nrc.uscg.mil/reports/rwservlet?standard_web+inc_seq=1036703">1036703</a><br/>Incident Time: 2013-01-26 09:00:00<br/>Nearest City: Harrodsburg, KY<br/>Location: 1063 LEXINTON RD. HARRODSBURG KY, 40330 1063 LEXINGTON RD<br/>Incident Type: FIXED<br/>Material: OIL, MISC: MOTOR<br/>Medium Affected: WATER<br/>Suspected Responsible Party: BOTTS GARAGE<br/><b>SkyTruth Analysis</b><br/>Lat/Long: 37.775223, -84.819129 (Approximated from street_address)<br/><b>Report Description</b><br/>***WEB REPORT*** SUSPECTED RESPONSIBLE PARTY RUNS A GARAGE AT THE GIVEN ADDRESS AND HAS DONE SO FOR OVER TWENTY YEARS, THERE IS OIL RESIDUE AROUND AND IN THE SEPTIC SYSTEM THAT IS CAUSING SERIOUS HEALTH HAZARDS TO THE LOCAL COMMUNITY KIDS AND ANIMALS. OIL, ANTIFREEZE AND OTHER AUTOMOTIVE CHEMICALS GET WASHED OFF THE FLOOR AND PUSHED INTO A FLOOR DRAIN THAT GOES INTO THE SEPTIC SYSTEM. THE PATH THAT IT TRAVELS IS ON THE GROUND AND THERE IS A TREMENDOUS AMOUNT OF OIL ABSORBED INTO THE GROUND, THERE IS A NASTY SMELL OF BACTERIA AND CHEMICAL AS YOU ENTER THE FRONT OF THE GARAGE. THERE HAS BEEN GAS DISPOSED OF ON THE GROUND BEHIND THE GARAGE AND WHERE THE OIL DISPOSAL CONTAINER IS LOCATED, IT HAS NO CONCRETE CONTAINMENT WALL OF ANY KIND AND OIL HAS FREELY DRAINED ON THE GROUND ALONG WITH GASOLINE, ANTIFREEZE AND OTHER TOXIC CHEMICALS. THIS FACILITY IS A HEALTH HAZARD AND NEEDS TO BE ADDRESSED A.S.A.P. AS STATED BEFORE, THE BUSINESS HAS OPERATED IN THAT LOCATION FOR OVER TWENTY YEARS UNDER THE SAME CONDITIONS, CARELESS AND NO RESPECT FOR THE ENVIRONMENT AND THE FUTURE OF OUR CHILDREN. THANK YOU FOR YOUR TIME.', 37.775222800000002, -84.819128899999995, 1, NULL, '2013-01-26 09:00:00', '2013-01-27 07:41:09.001', '{144}', '{NRC,mechanical,release}', '0101000020E6100000572D9F9B6C3455C0898E2E803AE34240', 1036703);
