INSERT INTO feedentry VALUES ('5bb7dcb4-6656-3536-8058-d91a4bdc22c4', 'NRC Report: Oil, Misc: Lubricating near Gregory, TX', 'http://www.nrc.uscg.mil/reports/rwservlet?standard_web+inc_seq=1036467', 'Incident Type: UNKNOWN SHEEN - NRC Report ID: 1036467 - Medium Affected: LAND - Suspected Responsible Party: ', '<b>Report Details</b><br/>NRC Report ID: <a href="http://www.nrc.uscg.mil/reports/rwservlet?standard_web+inc_seq=1036467">1036467</a><br/>Incident Time: 2013-01-23 15:00:00<br/>Nearest City: Gregory, TX<br/>Location: DITCH BEHIND THE SALVAGE YARD 4633 HWY 361<br/>Incident Type: UNKNOWN SHEEN<br/>Material: OIL, MISC: LUBRICATING<br/>Medium Affected: LAND<br/>Suspected Responsible Party: <br/><b>SkyTruth Analysis</b><br/>Lat/Long: 27.916418, -97.291554 (Approximated from ZIP)<br/>Reported Spill Volume: 187 gallons<br/><b>Report Description</b><br/>***WEB REPORT*** AT APPROXIMATELY 15:00 ON JANUARY 23, 2013, A SHERWIN ALUMINA EMPLOYEE DISCOVERED LUBE OIL MIXED WITH WATER IN THE DITCH BEHIND THE SALVAGE YARD.  THE SPILL IS APPROXIMATELY 187 GALLONS, IS CONTAINED, BEING VACUUMED OUT, AND THE OIL WILL BE SENT TO H&H OIL.  ANY CONTAMINATED SOIL WILL BE EXCAVATED AND SENT FOR DISPOSAL TO US ECOLOGY.  THE CAUSE IS UNKNOWN BUT BEING INVESTIGATED BY PLANT SECURITY.  A SAMPLE OF THE OIL WILL BE TAKEN AND SENT FOR ANALYSIS TO HELP DETERMINE THE SOURCE.', 27.9164175446667, -97.2915539095399, 1, NULL, '2013-01-23 15:00:00', '2013-01-25 07:41:11.003', '{170,126}', '{NRC,BigSpill,mechanical,release}', '0101000020E6100000AB9FBAD1A85258C0F8D217579AEA3B40', 1036467);
INSERT INTO feedentry VALUES ('3079d913-a10f-3d2f-8080-04e405e7e496', 'NRC Report: Creosote (K001) near Diboll, TX', 'http://www.nrc.uscg.mil/reports/rwservlet?standard_web+inc_seq=1036496', 'Incident Type: FIXED - NRC Report ID: 1036496 - Medium Affected: SUBSURFACE - Suspected Responsible Party: TEMPLE-INLAND FOREST PRODUCTS CORP.', '<b>Report Details</b><br/>NRC Report ID: <a href="http://www.nrc.uscg.mil/reports/rwservlet?standard_web+inc_seq=1036496">1036496</a><br/>Incident Time: 2013-01-23 15:00:00<br/>Nearest City: Diboll, TX<br/>Location: 303 S. TEMPLE DR.<br/>Incident Type: FIXED<br/>Material: CREOSOTE (K001)<br/>Medium Affected: SUBSURFACE<br/>Suspected Responsible Party: TEMPLE-INLAND FOREST PRODUCTS CORP.<br/><b>SkyTruth Analysis</b><br/>Lat/Long: 31.178663, -94.783374 (Approximated from street_address)<br/><b>Report Description</b><br/>THE CALLER IS REPORTING A RELEASE OF AN UNKNOWN AMOUNT OF CREOSOTE WASTE (K001) INTO SOIL.  THE CALLER STATED THAT AN UNDERGROUND STAINLESS STEEL LINE BROKE CAUSING A RELEASE INTO SOIL.  THE CALLER STATED THAT THE LAND IS APART OF A OLD LEGACY/POST-CLOSURE LAND USED FOR PUMP AND TREATMENT OF THE MATERIAL FOR THE GROUND AND USED ON TELEPHONE POLES.', 31.178662599999999, -94.783374499999994, 1, NULL, '2013-01-23 15:00:00', '2013-01-25 07:41:11.002', '{170}', '{NRC,release}', '0101000020E61000004D81CCCE22B257C0B10408D5BC2D3F40', 1036496);
